Scripts.OnShow:
SS_Stats_DrawValue('power', SS_Stats_Menu_Stat_Power);
SS_Stats_DrawValue('accuracy', SS_Stats_Menu_Stat_Accuracy);
SS_Stats_DrawValue('wisdom', SS_Stats_Menu_Stat_Wisdom);
SS_Stats_DrawValue('empathy', SS_Stats_Menu_Stat_Empathy);
SS_Stats_DrawValue('morale', SS_Stats_Menu_Stat_Morale);
SS_Stats_DrawValue('mobility', SS_Stats_Menu_Stat_Mobility);
SS_Stats_DrawValue('precision', SS_Stats_Menu_Stat_Precision);
SS_Stats_Menu_Points_Value:SetText(SS_Stats_GetAvaliablePoints());

self.addBtn:SetScript("OnClick", function()
  SS_Stats_AddPoint(1, 'power', SS_Stats_Menu_Stat_Power)
end);
self.reduceBtn:SetScript("OnClick", function()
  SS_Stats_AddPoint(-1, 'power', SS_Stats_Menu_Stat_Power)
end);
self.removeBtn:SetScript("OnClick", function()
  SS_Stats_AddPoint(-SS_Stats_GetValueWithoutModifier('power'), 'power', SS_Stats_Menu_Stat_Power)
end);
self.infoBtn:SetScript("OnClick", function()
  if (SS_Stats_Menu_Info:IsVisible() and SS_Stats_Menu_Info.title:GetText() == SS_Locale('power')) then
    SS_Stats_Menu_Info:Hide();
  else
    SS_Draw_StatInfo('power', 'Облегчает проверки ближнего боя и атлетики.');
  end;
end);

self.addBtn:SetScript("OnClick", function()
